mapper.select:
select count(*)
from Participate
where uid=#{uid} and pid=#{pid}

select count(*)
from Participate
where pid=#{pid}

mapper.update:
update Participate
set success_date = success_date+1
where uid=#{uid} and pid=#{pid}

mapper.select:
select total_date as totalDate
from Board
where pid=#{pid}

select success_date as successDate
from Participate
where uid=#{uid} and pid=#{pid}
